mapper.select:
SELECT * FROM prruSignal 
WHERE 
    userId = #{userId}
    and type = #{type}
    and timestamp = (
        SELECT timestamp 
        FROM prruSignal 
        WHERE timestamp > #{timestamp} and userId = #{userId}
        ORDER BY timestamp 
        LIMIT 1
    )

SELECT * FROM prruSignal 
WHERE 
    userId = #{userId} 
    and type = #{type}
    and timestamp = (
        SELECT timestamp 
        FROM prruSignal 
        WHERE userId = #{userId}
        ORDER BY timestamp desc 
        LIMIT 1
    )

        SELECT * FROM prruSignal 
        WHERE 
            userId = #{userId} 
            and type = #{type}
            and timestamp = (
                SELECT timestamp 
                FROM prruSignal 
                WHERE timestamp > #{timestamp} and userId = #{userId}
                ORDER BY timestamp desc 
				LIMIT 1		
            )

SELECT prruSignal.* 
FROM prruSignal join (
        SELECT distinct(timestamp) 
        FROM prruSignal 
        WHERE userId = #{userId} and type = #{type}
        ORDER BY timestamp desc 
        LIMIT 2
    ) a
WHERE prruSignal.userId = #{userId} and prruSignal.timestamp = a.timestamp;

SELECT prruSignal.* 
FROM prruSignal join (
        SELECT distinct(timestamp) 
        FROM prruSignal 
        WHERE userId = #{userId} and type = #{type}
        ORDER BY timestamp desc 
        LIMIT ${count}
    ) a
WHERE prruSignal.userId = #{userId} and prruSignal.timestamp = a.timestamp;

SELECT a.*,b.gpp,b.featureValue 
FROM prruFeature a join (
        SELECT * 
        FROM prruFeatureDetail 
        WHERE gpp in

            #{item}

    ) b on a.id = b.featureId
WHERE a.floorNo = #{floorNo};

SELECT a.*,b.gpp,b.featureValue FROM prruFeature a join ( SELECT * FROM prruFeatureDetail WHERE gpp in
#{item}
) b on a.id = b.featureId

mapper.delete:
    DELETE prrufeature,prrufeaturedetail 
    from prrufeature LEFT JOIN prrufeaturedetail on prrufeature.id = prrufeaturedetail.featureId 
    where prrufeature.userId = 'simulate'

		and prrufeature.floorNo=#{floorNo} 


		and prrufeature.eNodeBid=#{eNodeBid}
